mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}, sort = #{sort},
where id = #{id}

mapper.select:
select d.*, c.name as categoryName from dish d left join category c on d.category_id = c.id
and d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc

select d.*, c.name as categoryName from dish d left join category c on d.category_id = c.id
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.sort asc, d.update_time desc
