mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, description = #{description}, image = #{image}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
        select *
        from setmeal
        
            
            
                and name like concat('%', #{name}, '%')
            

            
            
                and category_id = #{categoryId}
            

            
            
                and status = #{status}
            
        
        order by update_time desc
